```python


class Solution:
    def threeSum(self, nums):
        """
        :type nums: List[int]
        :rtype: List[List[int]]
        """
        out = []
        zero = 0
        positive, negative = {}, {}
        p_max = 0
        n_min = 0
        import itertools
        if not nums:
            return out

        for i in nums:
            if i > 0:
                positive[i] = positive.get(i, 0) + 1
                if i > p_max:
                    p_max = i
            elif i < 0:
                negative[i] = negative.get(i, 0) + 1
                if i < n_min:
                    n_min = i
            else:
                zero += 1
        if len(positive) == 0 or len(negative) == 0:
            if zero > 2:
                out.append([0, 0, 0])
            return out

        p = sorted(positive.keys())
        for i in range(len(p)):  # 第一个 for
            if p[i] > -n_min:
                break
            for j in range(i+1, len(p)):
                s = p[i]+p[j]
                if s > -n_min:
                    break
                if -s in negative:
                    out.append([p[i], p[j], -s])

            if positive[p[i]] >= 2:
                if -(p[i]*2) in negative:
                    out.append([p[i], p[i], -(p[i]*2)])

        n = sorted([-k for k in negative])
        for i in range(len(n)): # 第二个 for
            if n[i] > p_max:
                break
            for j in range(i + 1, len(n)):
                s = n[i] + n[j]
                if s > p_max:
                    break
                if s in positive:
                    out.append([-n[i], -n[j], s])

            if negative[-n[i]] >= 2:
                if (n[i]*2) in positive:
                    out.append([-n[i], -n[i], (n[i]*2)])


        if zero > 0:
            for i in positive:
                if -i in negative:
                    out.append([i,0,-i])
        if zero > 2:
            out.append([0, 0, 0])

        return out


